import React, { useState } from 'react'
import { Box, Button, InputLabel, TextField, Typography } from '@mui/material'
const CreateBlog = () => {
    const [inputs, setInputs] = useState({
        title: '',
        description: '',
        image: ''
    })
    // input change
    const handleChange = (e) => {
        setInputs(prevState => ({
            ...prevState,
            [e.target.name]: e.target.value
        }))
    }
    // form
    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(inputs);
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <Box width={'50%'} border={3} borderRadius={10} padding={3} margin="auto" boxShadow={'10px 10px 20px #ccc'} display="flex" flexDirection={'column'} marginTop="30px" >
                    <Typography variant='h2' textAlign={'center'} fontWeight="bold" padding={3} color='gray' >Create A Post</Typography>
                    <InputLabel sx={{ mb: 1, mt: 2, fontSize: '24px', fontWeight: 'bold' }} >Title</InputLabel>
                    <TextField name='title' value={inputs.title} onChange={handleChange} margin='normal' variant='outlined' />
                    <InputLabel sx={{ mb: 1, mt: 2, fontSize: '24px', fontWeight: 'bold' }} >Description</InputLabel>
                    <TextField name='description' value={inputs.description} onChange={handleChange} margin='normal' variant='outlined' />
                    <InputLabel sx={{ mb: 1, mt: 2, fontSize: '24px', fontWeight: 'bold' }} >Image URL</InputLabel>
                    <TextField name='image' value={inputs.image} onChange={handleChange} margin='normal' variant='outlined' />
                    <Button type='submit' color='primary' variant='contained' >SUBMIT</Button>
                </Box>
            </form>
        </>
    )
}

export default CreateBlog
